#include <fstream>
#include <string>

using namespace std;

int main()
{
    string filename;
    string word;
    int count = 0;

    cout << "ファイル名を入力してください: ";
    cin >> filename;
    cout << "カウントしたい単語を入力してください: ";
    cin >> word;

    ifstream ifs(filename);
    if (!ifs) {
        cout << "ファイルが開けませんでした。" << endl;
        return 1;
    }

    string line;
    while (getline(ifs, line)) {
        if (line.find(word) != string::npos) {
            count++;
        }
    }

    cout << word << "は" << count << "回出現しました。" << endl;

    return 0;
}

